import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/lib/mongodb-mcp-client.ts.content:
// Frontend MCP Client that communicates with local MCP server process
interface ClusterConfig {
  clusterName: string;
  cloudProvider: string;
  region: string;
  tier: string;
}

interface MCPResponse {
  success: boolean;
  message: string;
  data?: any;
  error?: string;
}

export class MongoDBMCPClient {
  public isConnected = false;
  private mcpServerPort = 3001;
  private responseHandlers: Map<string, (response: any) => void> = new Map();

  constructor() {}

  async initialize(): Promise<void> {
    console.log("Initializing Frontend â†’ MCP Server connection...");

    try {
      // Test connection to local MCP server
      const response = await fetch(`http://localhost:${this.mcpServerPort}/health`);
      if (response.ok) {
        this.isConnected = true;
        console.log("Frontend â†’ MCP Server connection established");
      } else {
        throw new Error('MCP Server not responding');
      }
    } catch (error) {
      // Start MCP server if not running
      console.log("Starting local MCP server...");
      await this.startMCPServer();
    }
  }

  private async startMCPServer(): Promise<void> {
    // Send request to start MCP server
    const response = await fetch('/api/start-mcp', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        credentials: {
          clientId: import.meta.env.VITE_MDB_MCP_API_CLIENT_ID,
          clientSecret: import.meta.env.VITE_MDB_MCP_API_CLIENT_SECRET,
          publicKey: import.meta.env.VITE_MONGODB_PUBLIC_KEY,
          privateKey: import.meta.env.VITE_MONGODB_PRIVATE_KEY
        }
      })
    });

    if (!response.ok) {
      throw new Error('Failed to start MCP server');
    }

    // Wait for server to be ready
    await new Promise(resolve => setTimeout(resolve, 3000));
    this.isConnected = true;
    console.log("MCP Server started and ready");
  }

  async createCluster(clusterName: string): Promise<MCPResponse> {
    if (!this.isConnected) {
      await this.initialize();
    }

    try {
      console.log(`Frontend â†’ MCP Server: Creating cluster "${clusterName}"...`);

      // Add timeout to prevent infinite waiting
      const controller = new AbortController();
      const timeoutId = setTimeout(() => {
        console.log('âŒ Frontend â†’ MCP Server timeout after 10 seconds');
        controller.abort();
      }, 10000);

      const response = await fetch(`http://localhost:${this.mcpServerPort}/create-cluster`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          clusterName: clusterName,
          cloudProvider: "AWS",
          region: "US_EAST_1",
          tier: "M10"
        }),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`MCP Server error: ${response.status} ${errorText}`);
      }

      const result = await response.json();
      console.log('MCP Server â†’ MongoDB Atlas: Cluster creation response:', result);

      // Log exact response to trace hardcoded error source
      console.log('ðŸ” RAW MCP Server Response:', JSON.stringify(result, null, 2));

      // Return the actual MCP server response without any transformation
      return result;

    } catch (error: any) {
      console.error('Frontend â†’ MCP Server error:', error);

      // Check if this is where the IP access error is coming from
      if (error.name === 'AbortError') {
        console.log('âŒ Request timed out - returning timeout error');
        return {
          success: false,
          message: 'Request timed out',
          error: 'MCP server request timed out'
        };
      }

      return {
        success: false,
        message: 'Failed to create cluster via MCP Server',
        error: error.message
      };
    }
  }

  // List clusters functionality removed as requested

  disconnect() {
    this.isConnected = false;
  }
}

// Create singleton instance
export const mcpClient = new MongoDBMCPClient();

